import { PLAY_GAME, TAI, XIU } from "../constants/xucXacConstant";
import { randomIntFreomInterval } from "../utils/randomNum";

let initialState = {
  luaChon: null,
  soLuotChoi: 0,
  soLuotThang: 0,
  mangXucXac: [
    {
      hinhAnh: "./gameXucXacImg/1.png",
      giaTri: 1,
    },
    {
      hinhAnh: "./gameXucXacImg/1.png",
      giaTri: 1,
    },
    {
      hinhAnh: "./gameXucXacImg/1.png",
      giaTri: 1,
    },
  ],
};

export const xucXacReducer = (state = initialState, action) => {
  switch (action.type) {
    case XIU:
      return { ...state, luaChon: XIU };
    case TAI:
      return { ...state, luaChon: TAI };
    case PLAY_GAME: {
      let cloneState = { ...state };
      let newMangXucXac = cloneState.mangXucXac.map((xucXac) => {
        let random = randomIntFreomInterval(1, 6);
        return {
          hinhAnh: `./gameXucXacImg/{$random}.png`,
          giaTri: random,
        };
      });

      return { cloneState, mangXucXac: newMangXucXac };
    }
    default:
      return { ...state };
  }
};
